"use strict";(self.webpackChunkdocus=self.webpackChunkdocus||[]).push([[4906],{6421:t=>{t.exports=JSON.parse('{"tag":{"label":"Contract Actions","permalink":"/docs/tags/contract-actions","allTagsPath":"/docs/tags","count":3,"items":[{"id":"engage/smart-contracts/buyback-contract/buyback-v2-contract-actions","title":"Contract Actions","description":"Buyback.sol is an upgradeable smart contract used to repurchase $FACTR tokens using USDC via Uniswap V3. It supports standard and custom buybacks, applies TWAP-based price checks, and locks acquired FACTR for 1 year before distribution to either predefined vaults or custom addresses.","permalink":"/docs/engage/smart-contracts/buyback-contract/buyback-v2-contract-actions"},{"id":"engage/smart-contracts/staking-contract/staking-v2-contract-actions","title":"Contract Actions","description":"Staking.sol is a modular, upgradeable smart contract that enables users to stake ERC20 tokens in predefined plans to earn rewards over time. Each plan has configurable parameters such as lock duration, APY, staking and reward tokens, and staking caps. Rewards are calculated dynamically and can be claimed at any time or upon unstaking.","permalink":"/docs/engage/smart-contracts/staking-contract/staking-v2-contract-actions"},{"id":"engage/smart-contracts/vesting-contract/vesting-contract-actions","title":"Contract Actions","description":"ERC20LazyVesting.sol is a smart contract for token vesting that utilizes off-chain Merkle proofs and on-chain validation to efficiently manage scalable vesting schedules. It allows beneficiaries or operators to claim vested tokens over time, based on predefined parameters encoded in Merkle trees.","permalink":"/docs/engage/smart-contracts/vesting-contract/vesting-contract-actions"}],"unlisted":false}}')}}]);